$(document).on('ready', function () {
                                                $('.slider-for').slick({
                                                    slidesToShow: 1,
                                                    arrows: false,
                                                    adaptiveHeight: true,
                                                    dots: true
                                                });

                                                $('.slider-nav').slick({
                                                    slidesToShow: 6,
                                                    slidesToScroll: 1,
                                                    asNavFor: '.slider-for',
                                                    centerMode: false,
                                                    focusOnSelect: true,
                                                    variableWidth: true,
                                                    prevArrow: $('.prev'),
                                                    nextArrow: $('.next'),

                                                });

                                                $(window).load(function () {
                                                    $('.slider-nav .slick-slide').each(function () {
                                                        var sh = $(this).find('img').height();
                                                        var sw = $(this).find('img').width();
                                                        if (sh > sw) {
                                                            $(this).addClass("v")
                                                        }
                                                    });
                                                })
                                                
                                                $(".cur").text(1);
                                                $(".tot").text($(".art_gallery .slick-dots li").length)
                                                $('.slider-for').on('afterChange', function (event, slick, currentSlide) {
                                                    $(".cur").text(currentSlide + 1)
                                                });
                                                $('.slick-arrow.prev').click(function () {
                                                    $(".slider-for").slick('slickPrev');
                                                });
                                                $('.slick-arrow.next').click(function () {
                                                    $(".slider-for").slick('slickNext');
                                                });
                                            });
                                        






